<?php
// This is a minimal autoload file for PHPMailer installed via Composer
// Normally, this file is generated by Composer automatically.
// If you want to create it manually, you need to include the Composer autoload logic here.

// For demonstration, this file will just include PHPMailer classes manually.
// You should replace this with the actual Composer autoload file.

spl_autoload_register(function ($class) {
    $prefix = 'PHPMailer\\PHPMailer\\';
    $base_dir = __DIR__ . '/phpmailer/src/';

    $len = strlen($prefix);
    if (strncmp($prefix, $class, $len) !== 0) {
        // Not a PHPMailer class
        return;
    }

    $relative_class = substr($class, $len);
    $file = $base_dir . str_replace('\\', '/', $relative_class) . '.php';

    if (file_exists($file)) {
        require $file;
    }
});
?>
